import React, { useState } from 'react'
import './App.css'

function loadScript(src) {
	return new Promise((resolve) => {
		const script = document.createElement('script')
		script.src = src
		script.onload = () => {
			resolve(true)
		}
		script.onerror = () => {
			resolve(false)
		}
		document.body.appendChild(script)
	})
}

function App() {
	const [name, setName] = useState('Riza')
	const [amt, setAmt] = useState('200')

	async function displayRazorpay() {
		const res = await loadScript('https://checkout.razorpay.com/v1/checkout.js')

		if (!res) {
			alert('Razorpay SDK failed to load. Are you online?')
			return
		}

		const data = await fetch('http://localhost:8000/razorpay', { method: 'POST', body: JSON.stringify({amount:{amt}})}).then((t) =>
			t.json()
		)
		console.log(data)

		const options = {
			key: 'rzp_test_EjcezZcjp2kQNI',
			currency: data.currency,
			amount: data.amount.toString(),
			order_id: data.id,
			name: name,
			description: 'Test for payment gateway',
			image: '',
			handler: function (response) {
				alert("Order successful with: \nPayment ID:" + response.razorpay_payment_id + "\nOrder ID:" + response.razorpay_order_id + "\nSignature:" + response.razorpay_signature)
			},
			prefill: {
				name,
				email: 'xyz@gmail.com',
				phone_number: '9898989898'
			}
		}
		const paymentObject = new window.Razorpay(options)
		paymentObject.open()
	}

	return (
		<div className="App">
			<header className="App-header">
				<input
				type="text"
				value= {name}
				onChange= {e => setName(e.target.value)}
				/>
				<input
				type="text"
				value={amt}
				onChange= {e => setAmt(e.target.value)}
				/>
				<button onClick={displayRazorpay}>
					Pay
				</button>
			</header>
		</div>
	)
}

export default App
